function respimg(){function o(e){var t=e.clientWidth;if(window.devicePixelRatio>1){t=t*2}var n=e.getAttribute("data-respimg-width").split(" ");var r=n.sort(function(e,t){if(parseInt(e)>parseInt(t)){return-1}else if(parseInt(e)<parseInt(t)){return 1}else{return 0}});var i=r[0];for(var s=0;s<r.length;s++){if(parseInt(r[s])>=parseInt(t)){i=r[s]}}return i}var e=document.getElementsByClassName("respimg");for(var t=0;t<e.length;t++){var n=e[t].getAttribute("data-respimg-src");var r=e[t].getAttribute("data-respimg-alt");if(r===null){r=""}var i=o(e[t]);n=n.replace("@width",i);var s='<img src ="'+n+'" alt="'+r+'" width="'+i+'" />';if(e[t].innerHTML!=s){e[t].innerHTML='<img src ="'+n+'" alt="'+r+'" />'}}document.body.onresize=function(e){respimg()}}